import React, { Component } from "react";
import {BrowserRouter as Router, Route, Redirect, Switch} from 'react-router-dom';
import {Card, CardImg, CardBody, CardText} from 'reactstrap';
import AssignLocation from './AssignLocation';

export function searchToCard(person, cardSelect) {
  const cardAttributes = [`Name: ${person.first} ${person.last}`,
                          `Sex: ${person.sex}`,
                          (person.age_min || person.age_max) && `Age: ${person.age_min}-${person.age_max} yrs`,
                          (person.height_min || person.height_max) && `Height: ${person.height_min}-${person.height_max} cm`,
                          (person.weight_min || person.weight_max) && `Weight: ${person.weight_min}-${person.weight_max} kg`,
                          person.description && `Description : ${person.description}`,
                          person.looked_for_by && `Looked for By: ${person.looked_for_by}`];
  return (
    <div>
      <Card onClick={() => cardSelect(person)}>
        <CardImg width="320" height="240" src={person.picture} alt="No picture available"/>
        <CardBody>
          {cardAttributes.map(attribute => <CardText>{attribute}</CardText>)}
        </CardBody>
      </Card>
    </div>
  );
}

export function personToCard(person, cardSelect) {
  const cardAttributes = [`Name: ${person.first} ${person.last}`,
                          `Sex: ${person.sex}`,
                          person.dob && `Date of Birth: ${person.dob}`,
                          person.height && `Height: ${person.height} cm`,
                          person.weight && `Weight: ${person.weight} kg`,
                          person.description && `Description : ${person.description}`,
                          person.campsite && `Currently assigned to : Campsite ${person.campsite}`];
  return (
    <div>
      <Card onClick={() => cardSelect(person)}>
        <CardImg width="320" height="240" src={person.picture} alt="No picture available"/>
        <CardBody>
          {cardAttributes.map(attribute => <CardText>{attribute}</CardText>)}
        </CardBody>
      </Card>
    </div>
  );
}

export class DisplayFound extends Component {
  constructor(props) {
    super(props);
    this.state = {...this.props.location.state};
    this.cardSelect = this.cardSelect.bind(this);
  }
  cardSelect(person) {
    console.log(person);
  }
  render() {
    const message = this.state.found.length > 1 ? `${this.state.found.length} potential matches found!`
                                                : '1 Potential Match Found!'
    return(
      <div>
        <h3>{message}</h3>
        <p></p>
        {this.state.found.map((person) => searchToCard(person, this.cardSelect))}
      </div>
    );
  }
}

const PersonFound = ({match}) => (
      <Router>
        <Route exact path={`${match.path}`} component={DisplayFound} />
        <Route path={`${match.path}/assign-location`} component={AssignLocation} />
      </Router>
    );

export default PersonFound;
