import React, { Component } from "react";

export default class PersonFound extends Component {
  constructor(props) {
    super(props);
    this.state = {
      found: this.props.found
    };
    this.handleClick = this.handleClick.bind(this);
    this.anyMatches = this.anyMatches.bind(this);
  }
  componentDidMount() {
    this.client = stitchClient;
    this.db = db;
    loginAnonymous();
    this.anyMatches();
  }
  async anyMatches() {
    this.moveOn = true;
    var matches = [];
    await this.db.collection('missing')
      .find({first : this.props.first}, { limit: 20 })
      .toArray()
      .then(people => {
        people.forEach(person => matches.push(person));
      })
      .catch(err => console.error(`Failed to find documents: ${err}`));
    this.setState({found : matches});
    this.personFound = !(this.state.found.length === 0);
  }
  handleClick() {
    console.log(this.props.first);
  }
  render() {
    return(
      <div>
      <h3>Loading...</h3>
      <button onClick={this.handleClick}>first</button>
      </div>
    );
  }
}
