import React, { Component, Fragment } from "react";

import CheckIn from './CheckIn';
import Search from './Search';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {nextpage: ""};
    this.handleCheck = this.handleCheck.bind(this);
    this.handleSearch = this.handleSearch.bind(this);
  }
  handleCheck() {
    this.setState({nextpage : "check-in"})
  }
  handleSearch() {
    this.setState({nextpage : "search"})
  }
  render() {
    return(
      <div>
       <button type="button" onClick={this.handleCheck}>New Check-in</button>
       <p></p>
       <button type="button" onClick={this.handleSearch}>New Search</button>
      </div>
    );
  }
}

export default App;
