import React, {Component, Fragment} from 'react';
import {BrowserRouter as Router, Route, Redirect} from 'react-router-dom';
import {Form, Field} from 'react-final-form';

import PersonFound from './PersonFound';
import Camera from './Camera';

const AdvancedSearch = (
  <Fragment>
  <div>
    <label>Age Range</label>
    {' '}
    0
    <Field
      name="age"
      component="input"
      type="range"
      min="0"
      max="100"
      onChange={(event) => {this.ageNow = event.target.value}}
    />
    100
    {' '}
    {this.ageNow}
  </div>
  <div>
    <label>Height</label>
    {' '}
    2'0"
    <Field
      name="height"
      component="input"
      type="range"
      min="24"
      max="86"
    />
    7'0"
  </div>
  <div>
    <label>Weight</label>
    {' '}
    50
    <Field
      name="weight"
      component="input"
      type="range"
      min="50"
      max="350"
    />
    350
  </div>
  <div>
    <label>Description</label>
    <Field
      name="description"
      component="textarea"
      placeholder="Description"
    />
  </div>
  </Fragment>
);

class SearchForm extends Component {
  constructor(props) {
    super(props);
    this.state = {
      picture: undefined,
      advanced: false,
      toHome: false,
    };
    this.storePictureURI = this.storePictureURI.bind(this);
  }
  storePictureURI(uri) {
    this.setState({picture: uri});
  }
  render() {
    const advanced = !this.state.advanced
    const showAdvanced = () => this.setState({advanced});
    const onSubmit = async (values) => {
      const pictureURI = this.state.picture;
      this.info = {...values, picture : pictureURI};
      console.log(values);
    }
      /*
      const pictureURI = this.state.picture;
      this.info = {...values, pictureURI};
      await this.db.collection('found')
        .find({$text : {first : this.info.first}}, { limit: 20 })
        .toArray()
        .then(people => {
          people.forEach(person => matches.push(person));
        })
        .catch(err => console.error(`Failed to find documents: ${err}`));
      this.setState({toHome: true});
    }
    if (this.state.toHome) {
      return <Redirect push to={{pathname: `${this.props.match.url}/add-missing`, state: {...this.info}}}/>
    }
    */
    return(
        <div>
        <h3>Search</h3>
        <Camera storePicture={(uri) => this.storePictureURI(uri)}/>
        <Form
          onSubmit={onSubmit}
          initialValues={{sex : 'Unknown'}}
          render={({ handleSubmit, form, submitting, pristine, values }) => (
          <form onSubmit={handleSubmit}>
            <div>
              <label>First Name</label>
              <Field
                name="first"
                component="input"
                type="text"
                placeholder="First Name"
              />
            </div>
            <div>
              <label>Middle Name</label>
              <Field
                name="middle"
                component="input"
                type="text"
                placeholder="Middle Name"
              />
            </div>
            <div>
              <label>Last Name</label>
              <Field
                name="last"
                component="input"
                type="text"
                placeholder="Last Name"
              />
            </div>
            <div>
              <label>Sex</label>
              <div>
                <label>
                <Field
                  name="sex"
                  component="input"
                  type="radio"
                  value="Male"
                />{' '}
                Male
                </label>
                <label>
                <Field
                  name="sex"
                  component="input"
                  type="radio"
                  value="Female"
                />{' '}
                Female
                </label>
                <label>
                <Field
                  name="sex"
                  component="input"
                  type="radio"
                  value="Unknown"
                />{' '}
                Unknown
                </label>
              </div>
            </div>
            <div>
              <button type="button" onClick={showAdvanced}>Advanced Search</button>
            </div>
            {this.state.advanced && AdvancedSearch}
            <div className="buttons">
                <button type="submit" disabled={submitting || pristine}>
                  Search
                </button>
                <button
                  type="button"
                  onClick={form.reset}
                  disabled={submitting || pristine}
                >
                  Reset
                </button>
              </div>
            </form>
          )}/>
        </div>
    );
  }
}

class AddMissing extends Component {
  render() {
    return(
      <h3>Add Missing</h3>
    );
  }
}

const Search = ({match}) => (
      <Router>
        <Route exact path={`${match.path}`} component={SearchForm} />
        <Route path={`${match.path}/found`} component={PersonFound} />
        <Route path={`${match.path}/add-missing`} component={AddMissing} />
      </Router>
    );

export default Search;
