import React, { Component } from "react";
import {BrowserRouter as Router, Route, Redirect, Switch} from 'react-router-dom';
import { personToCard } from './PersonFound';
import { db } from '../stitch/database';

export default class FinishCheckIn extends Component {
  constructor(props) {
    super(props);
    this.state = {...this.props.location.state};
    this.toNext = false
    this.addFound = this.addFound.bind(this);
    this.refill = this.refill.bind(this);
  }
  refill() {
    this.nextPage = <Redirect push to={{pathname : '/check-in', state : {...this.state}}}/>;
    this.toNext = true;
    this.setState(this.state);
  }
  addFound() {
    const person = {...this.state};
    db
      .collection("found")
      .insertOne(person)
      .catch(console.error);
    alert('Check-in Successful! Redirecting home');
    this.nextPage = <Redirect push to='/'/>;
    this.toNext = true;
    this.setState(this.state);
  }
  render() {
    if(this.toNext) {
      return this.nextPage
    }
    return(
      <div>
        <h2>Finished Checking in {this.state.first} {this.state.last}</h2>
        <p></p>
        {personToCard(this.state)}
        <p></p>
        <h3>Is there anyone you'd like to search for, {this.state.first}?</h3>
        <button onClick={this.addFound}>Yes, finish check-in!</button>
        <button onClick={this.refill}>No</button>
      </div>
    );
  }
}
