import React, { Component } from "react";
import {
  Stitch,
  AnonymousCredential,
  RemoteMongoClient
} from "mongodb-stitch-browser-sdk";

class App extends Component {
  constructor() {
    super();
    this.state = {
      people: [],
      owner_id: undefined,
      first: undefined,
      last: undefined,
      mi: undefined,
      dob: undefined,
      sex: undefined,
      height: undefined,
      weight: undefined,
      campsite: undefined,
      description: undefined,
      picture: undefined,
    };
    this.handleChange = this.handleChange.bind(this);
    this.handleImage = this.handleImage.bind(this);
    this.getVideo = this.getVideo.bind(this);
    this.getPicture = this.getPicture.bind(this);
    this.confirmPicture = this.confirmPicture.bind(this);
    this.displayFound = this.displayFound.bind(this);
    this.addFound = this.addFound.bind(this);
  }

  componentDidMount() {
    this.client = Stitch.initializeDefaultAppClient("resp-nqkab");
    const mongodb = this.client.getServiceClient(
      RemoteMongoClient.factory,
      "mongodb-atlas"
    );
    this.db = mongodb.db("sample_disaster");
    this.displayPeopleOnLoad();
  }
  displayFound() {
    this.db
      .collection("found")
      .find({}, { limit: 1000 })
      .asArray()
      .then(people => {
        this.setState({people});
      });
   }
   displayPeopleOnLoad() {
    this.client.auth
      .loginWithCredential(new AnonymousCredential())
      .then(this.displayFound)
      .catch(console.error);
  }
  addFound(event) {
    event.preventDefault();
    this.db
      .collection("found")
      .insertOne({
        owner_id: this.client.auth.user.id,
        first: this.state.first,
        last: this.state.last,
        mi: this.state.mi,
        dob: this.state.dob,
        sex: this.state.sex,
        height: this.state.height,
        weight: this.state.weight,
        campsite: this.state.campsite,
        description: this.state.description,
        picture: this.state.picture
      })
      .then(this.displayFound)
      .catch(console.error);
    this.clearForm();
  }
  clearForm() {
    document.getElementById("checkin").reset();
  }
  handleChange(event) {
    const target = event.target;
    const name = target.name;
    const value = target.value;
    this.setState({ [name] : value});
  }
  handleImage(event) {
    var input = event.target;
    var reader = new FileReader();
    reader.onload = (() => {
      var arrayBuffer = reader.result;
      console.log(arrayBuffer);
    });
    reader.readAsArrayBuffer(input.files[0]);
    console.log(reader.result);
  }
  getVideo() {
  this.video = document.querySelector('.player');
  navigator.mediaDevices.getUserMedia({ video: { facingMode: "environment" }, audio: false })
    .then(localMediaStream => {
      this.video.srcObject = localMediaStream;
      this.video.play();
    })
    .catch(err => {
      console.error(`Error with getting media:`, err);
    });
  }
  getPicture() {
    this.video.pause();
    this.canvas = document.querySelector('.picture');
    this.ctx = this.canvas.getContext('2d');
    const width = this.video.videoWidth;
    const height = this.video.videoHeight;
    this.canvas.width = width;
    this.canvas.height = height;
    this.ctx.drawImage(this.video, 0, 0, width, height);
    const pixels = this.ctx.getImageData(0, 0, width, height);
    let imageContainer = {image : pixels};
    console.log(JSON.stringify(imageContainer));
    this.setState({picture : pixels});
  }
  confirmPicture() {
    this.video.srcObject.getTracks()[0].stop();
    console.log(this.state.picture);
  }
  render() {
    return (
      <div>
        <h3>CheckIn</h3>
        <form onSubmit={this.addFound} id='checkin'>
          <label>
            First Name:
             <input name="first" type="text" value={this.state.first} onChange={this.handleChange} />
          </label>
          <p></p>
          <label>
            Last Name:
             <input name="last" type="text" value={this.state.last} onChange={this.handleChange} />
          </label>
          <p></p>
          <label>
            Middle Initial:
             <input name="mi" type="text" value={this.state.mi} onChange={this.handleChange} />
          </label>
          <p></p>
          <label>
            Date of Birth:
             <input name="dob" type="text" value={this.state.dob} onChange={this.handleChange} />
          </label>
          <p></p>
          <label>
            Sex:
             <input name="sex" type="text" value={this.state.sex} onChange={this.handleChange} />
          </label>
          <p></p>
          <label>
            Height:
             <input name="height" type="text" value={this.state.height} onChange={this.handleChange} />
          </label>
          <p></p>
          <label>
            Weight:
             <input name="weight" type="text" value={this.state.weight} onChange={this.handleChange} />
          </label>
          <p></p>
          <label>
            Campsite:
             <input name="campsite" type="text" value={this.state.campsite} onChange={this.handleChange} />
          </label>
          <p></p>
          <label>
            Description:
             <input name="description" type="text" value={this.state.description} onChange={this.handleChange} />
          </label>
          <p></p>
          <video class="player" ></video>
          <button onClick={this.getVideo}>Open Camera</button>
          <button onClick={this.getPicture}>Take Picture</button>
          <button onClick={this.confirmPicture}>Confirm Picture</button>
          <canvas class="picture"></canvas>
          <p></p>
          <input type="submit" value="Submit" />
        </form>
      </div>
    );
  }
}

export default App;
