import React, {Component} from 'react';
import {Router, Route, IndexRoute} from 'react-router';
import {stitchClient, found, missing} from './../Stitch/Database';
import {addFound} from './../Stitch/Functions'
import {loginAnonymous} from './../Stitch/Authentication'
import {
  Stitch,
  RemoteMongoClient
} from 'mongodb-stitch-browser-sdk';

export default class CheckIn extends Component {
  constructor() {
    super();
    this.state = {
      first: undefined,
      last: undefined,
      mi: undefined,
      dob: undefined,
      sex: undefined,
      height: undefined,
      weight: undefined,
      race: undefined,
      campsite: undefined,
      description: undefined,
      image: undefined
    };
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }
  componentDidMount(){
    Stitch.initializeDefaultAppClient('resp-nqkab');
    const mongodb = this.client.getServiceClient(
      RemoteMongoClient.factory,
      "mongodb-atlas"
    );
  }
  handleChange(event) {
    const target = event.target;
    const value = target.value;
    const name = target.name;
    this.setState({[name]: value});
  }
  handleSubmit(event) {
    loginAnonymous()
      .then(found.insertOne({
        "first": this.state.first,
        "last": this.state.last,
        "mi": this.state.mi,
        "dob": this.state.dob,
        "sex": this.state.sex,
        "height": this.state.height,
        "weight": this.state.weight,
        "race": this.state.race,
        "campsite": this.state.campsite,
        "description": this.state.description,
        "image": this.state.image
      }));
  }
  render() {
    return(
      <div>
        <h3>CheckIn</h3>
        <form onSubmit={this.handleSubmit}>
          <label>
            First Name:
             <input name="first" type="text" value={this.state.first} onChange={this.handleChange} />
          </label>
          <p></p>
          <label>
            Last Name:
             <input name="last" type="text" value={this.state.last} onChange={this.handleChange} />
          </label>
          <p></p>
          <label>
            Middle Initial:
             <input name="mi" type="text" value={this.state.mi} onChange={this.handleChange} />
          </label>
          <p></p>
          <label>
            Date of Birth:
             <input name="dob" type="text" value={this.state.dob} onChange={this.handleChange} />
          </label>
          <p></p>
          <label>
            Sex:
             <input name="sex" type="text" value={this.state.sex} onChange={this.handleChange} />
          </label>
          <p></p>
          <label>
            Height:
             <input name="height" type="text" value={this.state.height} onChange={this.handleChange} />
          </label>
          <p></p>
          <label>
            Weight:
             <input name="weight" type="text" value={this.state.weight} onChange={this.handleChange} />
          </label>
          <p></p>
          <label>
            Campsite:
             <input name="race" type="text" value={this.state.race} onChange={this.handleChange} />
          </label>
          <p></p>
          <label>
            Description:
             <input name="description" type="text" value={this.state.description} onChange={this.handleChange} />
          </label>
          <p></p>
          <label>
            Image:
             <input name="image" type="text" value={this.state.image} onChange={this.handleChange} />
          </label>
          <p></p>
          <input type="submit" value="Submit" />
        </form>
      </div>
    );
  }
}
