import React, {Component} from 'react';
import { stitchClient, db } from '../stitch/database';
import { loginAnonymous } from '../stitch/auth';
import {BrowserRouter as Router, Route, Link, Redirect} from 'react-router-dom';
import { Formik } from 'formik';
import PersonFound from './PersonFound';
import AssignLocation from './AssignLocation';
import Camera from './Camera';

export default class CheckIn extends Component {
  constructor(props) {
    super(props);
    this.state = {
      owner_id: undefined,
      first: undefined,
      last: undefined,
      mi: undefined,
      dob: undefined,
      sex: undefined,
      height: undefined,
      weight: undefined,
      description: undefined,
      picture: undefined,
      found: [],
    };
    this.handleFill = this.handleFill.bind(this);
    this.storePictureURI = this.storePictureURI.bind(this);
    this.anyMatches = this.anyMatches.bind(this);
  }

  componentDidMount() {
    this.client = stitchClient;
    this.db = db;
    loginAnonymous();
  }

  handleFill(event) {
    const target = event.target;
    const name = target.name;
    const value = target.value;
    this.setState({ [name] : value});
  }

  storePictureURI(uri) {
    this.setState({picture: uri});
  }

  async anyMatches() {
    var matches = [];
    await this.db.collection('missing')
      .find({$and : [{first : this.state.first},
                     {$or : [{sex : this.state.sex}, {sex : "undefined"}]},
                     {weight : {$lte : this.state.weight, $gte : this.state.weight}}]}, { limit: 20 })
      .toArray()
      .then(people => {
        people.forEach(person => matches.push(person));
      })
      .catch(err => console.error(`Failed to find documents: ${err}`));
    this.setState({found : matches});
    this.personFound = !(this.state.found.length === 0);
  }

  render() {
    return (
        <Router>
        <div>
          <h3>Check-In</h3>
          <form id='checkin'>
            <label>
              First Name:
               <input name="first" type="text" value={this.state.first} onChange={this.handleFill} />
            </label>
            <p></p>
            <label>
              Last Name:
               <input name="last" type="text" value={this.state.last} onChange={this.handleFill} />
            </label>
            <p></p>
            <label>
              Middle Initial:
               <input name="mi" type="text" value={this.state.mi} onChange={this.handleFill} />
            </label>
            <p></p>
            <label>
              Date of Birth:
               <input name="dob" type="text" value={this.state.dob} onChange={this.handleFill} />
            </label>
            <p></p>
            <label>
              Sex:
               <input name="sex" type="text" value={this.state.sex} onChange={this.handleFill} />
            </label>
            <p></p>
            <label>
              Height:
               <input name="height" type="text" value={this.state.height} onChange={this.handleFill} />
            </label>
            <p></p>
            <label>
              Weight:
               <input name="weight" type="text" value={this.state.weight} onChange={this.handleFill} />
            </label>
            <p></p>
            <label>
              Description:
               <input name="description" type="text" value={this.state.description} onChange={this.handleFill} />
            </label>
            <p></p>
              Take Picture:
              <Camera storePicture={(uri) => this.storePictureURI(uri)}/>
            <p></p>
            <Link to='/check-in/next'><button type="button" onClick={this.anyMatches}>Next</button></Link>
          </form>
          <Route path='/check-in/next' render={() =>
            this.personFound ? <Redirect to={{pathname : '/check-in/found', state : {...this.state}}}/>
                             : <Redirect to={{pathname : '/check-in/assign-location', state : {...this.state}}}/>}/>
          <Route path='/check-in/assign-location' component={AssignLocation}/>
          <Route path='/check-in/found' component={PersonFound}/>
        </div>
        </Router>
    );
  }
}
