import React, { Component } from "react";
import { stitchClient, db } from '../stitch/database';
import { personToCard } from './PersonFound';
import Camera from './Camera';
import FinishCheckIn from './FinishCheckIn';
import {BrowserRouter as Router, Route, Redirect, Switch} from 'react-router-dom';
import { Modal, ModalHeader, ModalBody, ModalFooter } from 'reactstrap';
import { Form, Field } from 'react-final-form';

function campsiteToButton(campsite) {
  return (<label>
            <Field
            name="campsite"
            component="input"
            type="radio"
            value={campsite.name}
            />{' '}
            Campsite {campsite.name}
          </label>);
}

class AssignCamp extends Component {
  constructor(props) {
    super(props);
    this.state = {...this.props.location.state};
    this.modal = false;
    this.toNext = false;
    this.addFound = this.addFound.bind(this);
  }
  componentDidMount() {
    this.person = {...this.state};
    this.getCampsites();
  }
  async getCampsites() {
    var campsites = []
    await db
      .collection("campsites")
      .find({})
      .toArray()
      .then(sites => {
        sites.forEach(site => campsites.push(site));
      })
      .catch(err => console.error(`Failed to find documents: ${err}`));
    this.campsites = campsites;
    console.log(this.campsites);
  }
  addFound(person) {
    db
      .collection("found")
      .insertOne(person)
      .catch(console.error);
    alert('Check-in Successful! Redirecting home');
    this.nextPage = <Redirect push to='/'/>;
    this.toNext = true;
    this.setState(this.state);
  }
  render() {
    const onSubmit = (values) => {
      this.person = {...this.person, campsite : values.campsite}
      delete this.person.found;
      toggle();
    };
    const toggle = () => {
      this.modal = !this.modal;
      this.setState(this.state);
    };
    if(this.state.toNext) {
      return <Redirect push to={{pathname: `${this.props.match.url}/finish`, state: {...this.person}}}/>
    }
    return(
      <div>
      <h3>Assign Location</h3>
      <div>
      <Form
        onSubmit={onSubmit}
        render={({ handleSubmit, form, submitting, pristine, values }) => (
        <form onSubmit={handleSubmit}>
          <div>
            {this.state.campsites.map(campsiteToButton).join()}
          </div>
          <div className="buttons">
              <button type="submit" disabled={submitting || pristine}>
                Search
              </button>
              <button
                type="button"
                onClick={form.reset}
                disabled={submitting || pristine}>
                Reset
              </button>
            </div>
        </form>
        )}/>
      </div>
        <Modal isOpen={this.modal} toggle={toggle}>
          <ModalHeader>Is this information correct?</ModalHeader>
          <ModalBody>{personToCard({...this.person})}</ModalBody>
          <ModalFooter>footer</ModalFooter>
        </Modal>
      </div>
    );
  }
}

const AssignLocation = ({match}) => (
      <Router>
        <Route exact path={`${match.path}`} component={AssignCamp} />
        <Route path={`${match.path}/finish`} component={FinishCheckIn} />
      </Router>
    );

export default AssignLocation;
