import React, { Component } from "react";
import {BrowserRouter as Router, Route, Redirect, Switch} from 'react-router-dom';
import {Card, CardImg, CardBody, CardText} from 'reactstrap';
import AssignLocation from './AssignLocation';

export function personToCard(person) {
  return (
    <div>
      <Card>
        <CardImg width="320" height="240" src={person.picture}/>
        <CardBody>
          <CardText>Name: {person.first} {person.last}</CardText>
          <CardText>Sex: {person.sex} Age: {person.age_min}-{person.age_max} yrs</CardText>
          <CardText>Height: {person.weight_min}-{person.weight_max} cm   Weight: {person.weight_min}-{person.weight_max} kg</CardText>
          <CardText>Description : {person.description}</CardText>
        </CardBody>
      </Card>
    </div>
  );
}

export function searchToCard(person) {
  return (
    <div>
      <Card>
        <CardImg width="320" height="240" src={person.picture}/>
        <CardBody>
          <CardText>Name: {person.first} {person.last}</CardText>
          <CardText>Sex: {person.sex}</CardText>
          <CardText>Date of Birth: {person.dob}</CardText>
          <CardText>Height : {person.height} cm Weight: {person.weight} kg</CardText>
          <CardText>Description : {person.description}</CardText>
          <CardText>Currently assigned to : Campsite {person.campsite}</CardText>
        </CardBody>
      </Card>
    </div>
  );
}

class DisplayFound extends Component {
  constructor(props) {
    super(props);
    this.state = {...this.props.location.state}
  }
  render() {
    return(
      <div>
        <h3>Person Found</h3>
        <p></p>
        {this.state.found.map(personToCard)}
      </div>
    );
  }
}

const PersonFound = ({match}) => (
      <Router>
        <Route exact path={`${match.path}`} component={DisplayFound} />
        <Route path={`${match.path}/assign-location`} component={AssignLocation} />
      </Router>
    );

export default PersonFound;
