import React, { Component } from "react";
import {Link, Route, Redirect, Switch} from 'react-router-dom';
import { personToCard } from './PersonFound';
import { db } from '../stitch/database';
import {Home} from './App';
import {SearchForm} from './Search';

export class Finish extends Component {
  constructor(props) {
    super(props);
    this.state = {...this.props.location.state};
    this.toNext = false;
  }

  render() {
    const toSearch = () => {
      this.nextPage = <Redirect to={{pathname : '/search', state : {looked_for_by : `${this.state.first} ${this.state.last}`, looked_for_by_id: this.state.id}}}/>;
      this.toNext = true;
      this.setState(this.state);
    }
    if(this.toNext) {
      return this.nextPage
    }
    return(
      <div>
        <h2>Finished Checking in {this.state.first} {this.state.last}!</h2>
        <p></p>
        {personToCard(this.state)}
        <p></p>
        <h3>Is there anyone you'd like to search for, {this.state.first}?</h3>
        <p></p>
        <button onClick={toSearch}>Yes</button><Link to='/'><button>No thank you</button></Link>
      </div>
    );
  }
}

const FinishCheckIn = ({match}) => (
      <Switch>
        <Route exact path={`${match.path}`} component={Finish}/>
        <Route path='/' component={Home}/>
        <Route path='/search' component={SearchForm}/>
      </Switch>
);

export default FinishCheckIn;
