import React, {Component, Fragment} from 'react';
import { stitchClient, db } from '../stitch/database';
import { loginAnonymous } from '../stitch/auth';
import camera from './camera.jpg';
import { Formik } from 'formik';

import PersonFound from './PersonFound';

export default class CheckIn extends Component {
  constructor(props) {
    super(props);
    this.state = {
      owner_id: undefined,
      first: undefined,
      last: undefined,
      mi: undefined,
      dob: undefined,
      sex: undefined,
      height: undefined,
      weight: undefined,
      description: undefined,
      picture: undefined,
      found: [],
    };
    this.handleFill = this.handleFill.bind(this);
    this.pictureControl = this.pictureControl.bind(this);
    this.getVideo = this.getVideo.bind(this);
    this.getPicture = this.getPicture.bind(this);
    this.confirmPicture = this.confirmPicture.bind(this);
    this.whatsNext = this.whatsNext.bind(this);
  }

  componentDidMount() {
    this.client = stitchClient;
    this.db = db;
    loginAnonymous();
  }

  handleFill(event) {
    const target = event.target;
    const name = target.name;
    const value = target.value;
    this.setState({ [name] : value});
  }

  pictureControl(status) {
    switch(status) {
      case "camera opened":
        return <button type="button" onClick={this.getPicture}>Take Picture</button>
      case "picture taken":
        return <Fragment>
                 <button type="button" onClick={this.confirmPicture}>Confirm Picture</button>
                 <button type="button" onClick={this.getVideo}>Try Again</button>
               </Fragment>
      default:
        return <img src={camera} onClick={this.getVideo}/>
    }
  }
  getVideo() {
  this.setState({picture_status : "camera opened"});
  this.video = document.querySelector('.player');
  navigator.mediaDevices.getUserMedia({ video: { facingMode: "environment" }, audio: false })
    .then(localMediaStream => {
      this.video.srcObject = localMediaStream;
      this.video.play();
    })
    .catch(err => {
      console.error(`Error with getting media:`, err);
    });
  }
  getPicture() {
    this.video.pause();
    this.video.srcObject.getTracks()[0].stop();
    this.canvas = document.createElement("canvas");
    const width = this.video.videoWidth;
    const height = this.video.videoHeight;
    this.canvas.width = width;
    this.canvas.height = height;
    this.canvas.style.left = this.video.offsetLeft + "px";
    this.canvas.style.top = this.video.offsetTop + "px";
    var ctx = this.canvas.getContext('2d');
    ctx.drawImage(this.video, 0, 0, width, height);
    this.setState({picture_status : "picture taken"});
  }
  confirmPicture() {
    const dataURL = this.canvas.toDataURL('image/jpeg');
    this.setState({picture : dataURL});
  }
  storePictureURI(uri) {
    this.setState({picture: uri});
  }
  render() {
    return (
      <div>
        <h3>CheckIn</h3>
        <form id='checkin'>
          <label>
            First Name:
             <input name="first" type="text" value={this.state.first} onChange={this.handleFill} />
          </label>
          <p></p>
          <label>
            Last Name:
             <input name="last" type="text" value={this.state.last} onChange={this.handleFill} />
          </label>
          <p></p>
          <label>
            Middle Initial:
             <input name="mi" type="text" value={this.state.mi} onChange={this.handleFill} />
          </label>
          <p></p>
          <label>
            Date of Birth:
             <input name="dob" type="text" value={this.state.dob} onChange={this.handleFill} />
          </label>
          <p></p>
          <label>
            Sex:
             <input name="sex" type="text" value={this.state.sex} onChange={this.handleFill} />
          </label>
          <p></p>
          <label>
            Height:
             <input name="height" type="text" value={this.state.height} onChange={this.handleFill} />
          </label>
          <p></p>
          <label>
            Weight:
             <input name="weight" type="text" value={this.state.weight} onChange={this.handleFill} />
          </label>
          <p></p>
          <label>
            Description:
             <input name="description" type="text" value={this.state.description} onChange={this.handleFill} />
          </label>
          <p></p>
            Take Picture:
            <Camera onClick={(uri) => this.storePictureURI(uri)}/>
          <p></p>
          <input type="submit" value="Next"/>
        </form>
      </div>
    );
  }
}

class Camera extends Component {
  constructor(props) {
    super(props);
    this.state = {
      picture: undefined,
      buttons: undefined,
    };
    this.canvas = document.querySelector('.picture');
    this.ctx = this.canvas.getContext('2d');
    this.pictureControl = this.pictureControl.bind(this);
    this.getVideo = this.getVideo.bind(this);
    this.getPicture = this.getPicture.bind(this);
    this.confirmPicture = this.confirmPicture.bind(this);
  }
  componentDidMount() {
    this.ctx.drawImage(camera, 0, 0);
    this.canvas.addEventListener('click', this.getVideo);
  }
  componentDidUnmount() {
    this.video.srcObject.getTracks()[0].stop();
  }
  getVideo() {
    this.canvas.removeEventListener('click', this.getVideo);
    this.video = document.createElement("video");
    navigator.mediaDevices.getUserMedia({ video: { facingMode: "environment" }, audio: false })
      .then(localMediaStream => {
        this.video.srcObject = localMediaStream;
        this.video.play();
      })
      .catch(err => {
        console.error(`Error with getting media:`, err);
      });
    this.setState({buttons : <button type="button" onClick={this.getPicture}>Take Picture</button>})
  }
  getPicture() {
    this.video.pause();
    const width = this.video.videoWidth;
    const height = this.video.videoHeight;
    this.canvas.width = width;
    this.canvas.height = height;
    this.canvas.style.left = this.video.offsetLeft + "px";
    this.canvas.style.top = this.video.offsetTop + "px";
    ctx.drawImage(this.video, 0, 0, width, height);
    const dataURL = this.canvas.toDataURL('image/jpeg');
    this.setState({buttons: <div>
                              <button type="button" onClick={this.props.onClick(dataURL)}>Confirm Picture</button>
                              <button type="button" onClick={this.getVideo}>Try Again</button>
                            </div>});
  }
  render() {
    return(
      <div>
        <canvas class='picture'></canvas>
        <p></p>
        {this.state.buttons}
      </div>
    );
  }
}

